import React, { useState } from "react";
import CheckoutForm from "./CheckoutForm";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const stripePromise = loadStripe(
  "pk_test_51QGIp4DuIWaWxstxGXGX55gb1znJ40StbDOv3y1um0N3RAhn2cHWHfIxE9PAeuif4cep4QaKFtql5pl1x5UiBmyH00rmCHJQmO"
);

const NgoSection = () => {
  const [selectedNgo, setSelectedNgo] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedPackage, setSelectedPackage] = useState(null);

  const ngos = [
    {
      name: "Smile Foundation",
      description:
        "Feeding, educating & supporting underprivileged children across India.",
      address: "123 NGO Lane, New Delhi, India",
      contact: "+91 98765 43210",
      image:
        "data:image/jpeg;base64,",
      packages: [
        { title: "Veg Biryani for 32 people", amount: 200 },
        { title: "Milk for 20 children", amount: 150 },
        { title: "Fruits & Snacks for 50 people", amount: 300 },
      ],
    },
    {
      name: "Smile Foundation",
      description:
        "Feeding, educating & supporting underprivileged children across India.",
      address: "123 NGO Lane, New Delhi, India",
      contact: "+91 98765 43210",
      image:"data:image/jpeg;base64,",
        packages: [
        { title: "Veg Biryani for 32 people", amount: 200 },
        { title: "Milk for 20 children", amount: 150 },
        { title: "Fruits & Snacks for 50 people", amount: 300 },
      ],
    },
    {
      name: "Akshaya Patra",
      description:
        "Serving 2M+ mid-day meals to school children every day across India.",
      address: "Plot 123, Bengaluru, Karnataka",
      contact: "+91 98765 12345",
      image:
        "data:image/jpeg;base64,",
      packages: [
        { title: "Meal for 25 students", amount: 180 },
        { title: "Milk & Biscuits for 30 kids", amount: 160 },
      ],
    },
  ];

  const handleDonateClick = (ngo) => {
    setSelectedNgo(ngo);
    setSelectedPackage(null);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedNgo(null);
    setSelectedPackage(null);
  };

  return (
    <section className="my-16 px-4 md:px-14">
      <ToastContainer position="top-right" autoClose={3000} />

      {/* Header */}
      <h2 className="text-4xl font-extrabold text-center bg-gradient-to-r from-purple-600 to-indigo-600 bg-clip-text text-transparent mb-14">
        Partner NGOs You Can Trust
      </h2>

      {/* NGO Cards */}
     <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-10">
  {ngos.map((ngo, index) => (
    <div
      key={index}
      className="group bg-white/90 backdrop-blur-xl rounded-3xl border border-gray-200 shadow-md hover:shadow-2xl transition-all duration-300 hover:-translate-y-2"
    >
      {/* Image */}
      <div className="overflow-hidden rounded-t-3xl">
        <img
          src={ngo.image}
          alt={ngo.name}
          className="w-full h-52 object-cover transition-transform duration-500 group-hover:scale-110"
        />
      </div>

      {/* Card Content */}
      <div className="p-7 flex flex-col h-full">
        <h3 className="text-xl font-extrabold text-purple-800 tracking-wide">
          {ngo.name}
        </h3>

        <p className="text-gray-600 mt-3 text-sm leading-relaxed">
          {ngo.description}
        </p>

        {/* Info */}
        <div className="mt-4 space-y-2 text-sm">
          <p className="flex items-start gap-2">
            <span className="font-semibold text-gray-700">Address:</span>
            <span className="text-gray-600">{ngo.address}</span>
          </p>

          <p className="flex items-start gap-2">
            <span className="font-semibold text-gray-700">Contact:</span>
            <span className="text-gray-600">{ngo.contact}</span>
          </p>
        </div>

        {/* Button */}
        <button
          onClick={() => handleDonateClick(ngo)}
          className="mt-6 w-full py-3 rounded-2xl bg-gradient-to-r from-purple-700 to-purple-900 text-white font-semibold shadow-md hover:shadow-lg transition-all"
        >
          Donate to {ngo.name}
        </button>
      </div>
    </div>
  ))}
</div>


      {/* Donation Modal */}
      {isModalOpen && selectedNgo && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 px-6">
          <div className="bg-white rounded-3xl p-8 shadow-2xl w-full max-w-md relative animate-fade-in">
            <button
              onClick={handleCloseModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl"
            >
              &times;
            </button>

            <h3 className="text-2xl font-bold text-purple-700 text-center mb-6">
              Donate to {selectedNgo.name}
            </h3>

            {/* Package Selection */}
            {!selectedPackage && (
              <div>
                <p className="font-semibold mb-2 text-gray-700">
                  Select a food package:
                </p>

                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  {selectedNgo.packages.map((pkg, idx) => (
                    <button
                      key={idx}
                      onClick={() => setSelectedPackage(pkg)}
                      className="p-4 border rounded-xl bg-gray-50 hover:bg-purple-50 hover:border-purple-300 transition-all"
                    >
                      <p className="font-semibold">{pkg.title}</p>
                      <p className="text-gray-600">â‚¹{pkg.amount}</p>
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Stripe Payment */}
            {selectedPackage && (
              <Elements stripe={stripePromise}>
                <CheckoutForm
                  ngoName={selectedNgo.name}
                  defaultAmount={selectedPackage.amount}
                  onPaymentSuccess={handleCloseModal}
                />
              </Elements>
            )}
          </div>
        </div>
      )}
    </section>
  );
};

export default NgoSection;
